olor=\"#FFFFFF\" button_bg_color=\"#48D1CC\" button_border_width=\"0px\" button_border_radius=\"100px\" button_font=\"Hind|700||on|||||\" custom_padding=\"18px|32px|18px|32px|true|true\" locked=\"off\" global_colors_info=\"{}\"][/et_pb_button][/et_pb_column][/et_pb_row][et_pb_row column_structure=\"1_2,1_2\" _builder_version=\"4.19.5\" _module_preset=\"default\" background_color=\"#F5F5F5\" custom_margin=\"||100px||false|false\" custom_padding=\"||40px||false|false\" global_colors_info=\"{}\"][et_pb_column type=\"1_2\" _builder_version=\"4.19.5\" _module_preset=\"default\" background_enable_color=\"off\" global_colors_info=\"{}\"][et_pb_image src=\"http://localhost:10033/wp-content/uploads/2023/01/mysterious-ascent-scaled.jpg\" title_text=\"DCIM100GOPROGOPR3052.JPG\" _builder_version=\"4.19.5\" _module_preset=\"default\" global_colors_info=\"{}\"][/et_pb_image][/et_pb_column][et_pb_column type=\"1_2\" _builder_version=\"4.19.5\" _module_preset=\"default\" global_colors_info=\"{}\"][et_pb_text _builder_version=\"4.19.5\" _module_preset=\"b327cbe4-b1d5-4b66-ad37-40c8f2f061ba\" header_2_font=\"Baskervville|700|||||||\" header_2_text_color=\"#005CC8\" header_2_font_size=\"48px\" header_2_line_height=\"1.35em\" text_orientation=\"center\" header_2_font_size_tablet=\"32px\" header_2_font_size_phone=\"20px\" header_2_font_size_last_edited=\"on|desktop\" locked=\"off\" global_colors_info=\"{}\"]<h1 style=\"text-align: center;\">MASTER PROGRAM</h1>[/et_pb_text][et_pb_text _builder_version=\"4.19.5\" _module_preset=\"default\" text_font=\"Montserrat||||||||\" width=\"100%\" custom_padding=\"|0px||||\" global_colors_info=\"{}\"]<p style=\"text-align: left;\">The Master program was designed to offer freedivers the opportunity to develop a fuller knowledge of their bodies aquatic capabilities in a semi-autonomous setting, under the guidance of an experienced instructor.</p>\n<p style=\"text-align: left;\">This detail-oriented series of workshops and lectures is aimed at expounding upon the freediving knowledge and techniques that were introduced during the courses.</p>\n<p style=\"text-align: left;\">After creating an initial training plan with our Instructor Trainer, your time will be split between theory sessions, coached training sessions (dry, pool and open water), autonomous training sessions, and free time in which<br />you will have the opportunity to explore the beautiful surroundings of the school, work on your stretching, and socialize with everyone at the school.</p>\n<p style=\"text-align: left;\"></p>\n<ul>\n<li style=\"text-align: left;\">Duration: 1 month</li>\n<li style=\"text-align: left;\">Prerequisite: SSI Freediving Level 2 or equivalent</li>\n<li style=\"text-align: left;\">Pricing: 1000 USD</li>\n</ul>[/et_pb_text][et_pb_button button_text=\"Recreational\" button_alignment=\"center\" _builder_version=\"4.19.5\" _module_preset=\"31b4f027-aaa0-4f2f-ae02-959764e19e4f\" custom_button=\"on\" button_text_size=\"14px\" button_text_color=\"#FFFFFF\" button_bg_color=\"#48D1CC\" button_border_width=\"0px\" button_border_radius=\"100px\" button_font=\"Hind|700||on|||||\" custom_padding=\"18px|32px|18px|32px|true|true\" locked=\"off\" global_colors_info=\"{}\"][/et_pb_button][/et_pb_column][/et_pb_row][/et_pb_section][et_pb_section fb_built=\"1\" admin_label=\"Footer\" _builder_version=\"4.19.5\" _module_preset=\"default\" background_color=\"rgba(0,0,0,0.87)\" background_enable_image=\"off\" custom_margin=\"0px|0px|0px|0px|false|false\" custom_padding=\"0px|0px|0px|0px|false|false\" saved_tabs=\"all\" collapsed=\"on\" global_colors_info=\"{}\"][et_pb_row _builder_version=\"4.19.5\" _module_preset=\"default\" custom_padding=\"20px||||false|false\" border_color_all=\"#FFDD67\" border_width_top=\"1px\" border_style_top=\"none\" global_colors_info=\"{}\"][et_pb_column type=\"4_4\" _builder_version=\"4.18.0\" _module_preset=\"default\" global_colors_info=\"{}\"][et_pb_menu menu_style=\"centered\" active_link_color=\"#FFFFFF\" logo=\"h